import { useState } from "react";

export default function TodoList() {
  const [todos, settodos] = useState([]);
  const [newTodo, setnewTodo] = useState("");

  //fonction ajoutant une tache
  const addTodo = () => {
    if (newTodo.trim() !== "") {
      settodos([...todos, newTodo]);
      setnewTodo("");
    }
  };

  return (
    <>
      <h1>Gestionnaire des taches</h1>
      <input type="texte" placeholder="ajouter une liste ici..." value={newTodo} onChange={(e) => setnewTodo(e.v)}  />
      <button onClick={addTodo}>Ajouter</button>

      {/* List des taches ajout√©es */}
      <ul>
          {todos.map((todos) => (<li>{todos}</li>))}
      </ul>
    </>
  );
}
