import { useState } from "react";

export default function TodoList() {

  const [TodoList, setTodoList] = useState([]);
  const [newTodo, setnewTodo] = useState("")

  //creation de la function ajouter une tache
  const addList = () =>{
     if(newTodo.trim() !== ""){
          setnewTodo([...TodoList, newTodo]);
          setnewTodo("")
     }

  }

  return (
     <>
       <h1>Liste des taches</h1>
       
         <input type="texte" placeholder="ajouter un tache ici" value={newTodo} />
         <button onChange={addList}>ajouter</button>
         <ul>
          {TodoList.map()}
         </ul>

     </>
   );

  }
